// The shop model will one day handle all sorts of fun stuff
// like shop caching, on-server storage, and other such cool things
// For now, though it is a simple skeleton

// TODO: put the following into a general conf file
var shopSite = "gamertiser.com";
var shopPath = "/api/v1/product.json?token=";
var shopToken = "cf242dcaf5d0a4a0b0e2949e804dcebf";
var shopPort = 80;

http = require( "http" );

var options = { 
	host: shopSite ,
	port: shopPort ,
	path: shopPath + shopToken
};

this.GetItems = function(){ 
	var dataJson = "";
	http.get( options, function(res){ 
		if( res.statusCode == 200 ) { 
			res.on( "data", function( chunk ){ 
				dataJson += chunk;
			} );
		}
		else {
			dataJson = res.statusCode;
		}
	} );
	return dataJson;
}



